class Auth{static baseURL(){return"https://ancient-caverns-16784.herokuapp.com"}
static getAccessToken(){let token=KeyHelper.keyRead('accessToken');if(token){return token}else{console.warn('no token found');return!1}}
static getAccessName(){let username=KeyHelper.keyRead('accessUsername');if(username){return username}else{console.warn('no username found');return!1}}
static userLogOut(){var HttpAddress_Get=this.baseURL()+"/auth/logout";var token=this.getAccessToken();return $.ajax({url:HttpAddress_Get,method:'GET',headers:{'X-Auth-Token':token}}).done(function(resolve){KeyHelper.keyRemove('accessUsername');KeyHelper.keyRemove('accessToken');return{resolve}}).fail(function(reject){console.warn('GetHttpRequest:','fail=',reject);if(!reject){reject={message:"Unknown Error"}}
return{reject}})}
static userRegister(options={}){var HttpAddress_Get=this.baseURL()+"/auth/register";KeyHelper.keySave({name:'accessUsername',value:options.username});return $.ajax({url:HttpAddress_Get,method:'POST',data:{'username':options.username,'password':options.password}}).done(function(resolve){KeyHelper.keySave({name:'accessToken',value:resolve.accessToken});if(!KeyHelper.keySave({name:'accessToken',value:resolve.accessToken})){return new Promise((resolve,reject)=>{reject({message:"Problem with handling storage"})})}else{return{resolve}}}).fail(function(reject){console.warn('GetHttpRequest:','fail=',reject);if(!reject){reject={message:"Unknown Error"}}
return{reject}})}
static userLogIn(options={}){var HttpAddress_Get=this.baseURL()+"/auth/login";KeyHelper.keySave({name:'accessUsername',value:options.username});return $.ajax({url:HttpAddress_Get,method:'POST',data:{'username':options.username,'password':options.password}}).done(function(resolve){KeyHelper.keySave({name:'accessToken',value:resolve.accessToken});if(!KeyHelper.keySave({name:'accessToken',value:resolve.accessToken})){return new Promise((resolve,reject)=>{reject({message:"Problem with handling storage"})})}else{return{resolve}}}).fail(function(reject){console.warn('GetHttpRequest:','fail=',reject);if(!reject){reject={message:"Unknown Error"}}
return{reject}})}}